import type { RequestHeaders } from "@octokit/types/dist-types/RequestHeaders";
import type { RequestMethod } from "@octokit/types/dist-types/RequestMethod";
import type { RequestRequestOptions } from "@octokit/types/dist-types/RequestRequestOptions";
import type { Url } from "@octokit/types/dist-types/Url";
/**
 * Generic request options as they are returned by the `endpoint()` method
 */
export type RequestOptions = {
    method: RequestMethod;
    url: Url;
    headers: RequestHeaders;
    body?: any;
    request?: RequestRequestOptions;
};
